const button=document.querySelector(".button-offer"),modal=document.querySelector(".modal"),arrival=document.querySelector(".input-arrival-date"),departure=document.querySelector(".input-of-departure"),formInput=document.querySelector(".search-hotel-form"),inputAdults=document.querySelector(".input-adults"),inputChildren=document.querySelector(".input-children");let storageAdultsSupport=!0,storageAdults="",storageChildrenSupport=!0,storageChildren="";try{storageAdults=localStorage.getItem("adults"),storageChildren=localStorage.getItem("children")}catch(e){storageAdultsSupport=!1,storageChildrenSupport=!1}button.addEventListener("click",function(e){console.log("клик по поиску гостиницы"),e.preventDefault(),modal.classList.toggle("modal-hide")}),formInput.addEventListener("submit",function(e){departure.value&&arrival.value&&inputAdults.value&&inputChildren.value?(storageAdultsSupport&&localStorage.setItem("adults",inputAdults.value),storageChildrenSupport&&localStorage.setItem("children",inputChildren.value)):(e.preventDefault(),console.log(" Нужно заполнить все поля "))});